(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds"),r=function(t){return String(t).padStart(2,"0")};let m=setInterval((()=>{let t=(()=>{let t=(new Date("23 june 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,hours:Math.floor(t/60/60),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();e.textContent=r(t.hours),n.textContent=r(t.minutes),o.textContent=r(t.seconds),t.timeRemaining<0&&(clearInterval(m),e.textContent="00",n.textContent="00",o.textContent="00")}),1e3)})()})();