(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let r;const m=function(t){return String(t).padStart(2,"0")},i=()=>{let t=(()=>{let t=(new Date("23 june 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,hours:Math.floor(t/60/60),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();e.textContent=m(t.hours),n.textContent=m(t.minutes),o.textContent=m(t.seconds),t.timeRemaining<0&&(clearInterval(r),e.textContent="00",n.textContent="00",o.textContent="00")};i(),r=setInterval(i,1e3)})()})();